catalog_ui_policy.script_false:
function onCondition() {
    g_form.setSectionDisplay('What amount your are requesting for flight booking?', false);
}

catalog_ui_policy.script_true:
function onCondition() {
    g_form.setSectionDisplay('What amount your are requesting for flight booking?', true);
    g_form.setMandatory('What amount your are requesting for flight booking?', true);
}
